
public class TaskVar {

	/**
	 * @param args
	 * 125 + 24 = 149
125 - 24 = 101
125 x 24 = 3000
125 / 24 = 5
125 mod 24 = 5
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numAdd;
		numAdd = 125 + 24;
		System.out.println(numAdd + " = 125 + 24");
		
		int numDeduct;
		numDeduct = 125 - 24;
		System.out.println(numDeduct + " = 125 - 24");
		
		int numMultiply;
		numMultiply = 125 * 24;
		System.out.println(numMultiply + " = 125 * 24");
	}

}
